import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Blog } from 'src/app/models/blog';
import { BlogService } from 'src/app/services/blog.service';


@Component({
  selector: 'app-edit-bblog',
  templateUrl: './edit-bblog.component.html',
  styleUrls: ['./edit-bblog.component.scss']
})
export class EditBblogComponent implements OnInit {
  blogId: number;
  blog: Blog;

  constructor(private route: ActivatedRoute, private blogService: BlogService, private router: Router) { }

  ngOnInit(): void {
    this.blogId = this.route.snapshot.params['id'];
    this.blogService.getBlog(this.blogId).subscribe(
      data => {
        this.blog = data;

      },
      error => console.log(error)
    );
  }

  updateBlog() {
    this.blogService.updateBlog(this.blog).subscribe(
      data => {
        console.log(data);
        this.router.navigate(['list-blog']);
        this.gotoList();
      },
      error => console.log(error)
    );
  }
  onSubmit() {
    this.updateBlog();    
  }
  gotoList() {
    this.router.navigate(['/list-blog']);
  }
}
<h1>Edit Blog</h1>
<form (ngSubmit)="onSubmit()" #blogForm="ngForm">
  <div class="form-group">
    <label for="title">Title</label>
    <input type="text" class="form-control" id="title" [(ngModel)]="blog.title" name="title" required>
  </div>
  <div class="form-group">
    <label for="content">Content</label>
    <textarea class="form-control" id="content" [(ngModel)]="blog.content" name="content" required></textarea>
  </div>
  <div class="form-group">
    <label for="publicationDate">Publication Date</label>
    <input type="Date" class="form-control" id="publicationDate"  [(ngModel)]="blog.publicationDate" name="publicationDate" required>
  </div>
  <div class="form-group">
    <button type="submit" class="btn btn-success">Save</button>
    <button type="button" class="btn btn-danger"  (click)="gotoList()">Cancel</button>
  </div>
</form>
