<h1>Edit Order</h1>

<form (ngSubmit)="onSubmit()">
  <div class="form-group">
    <label for="message">Adress</label>
    <textarea class="form-control" id="address" [(ngModel)]="Order.address" name="address"></textarea>
  </div>
  <div class="form-group">
    <label for="recipient">Reference</label>
    <input type="text" class="form-control" id="reference" [(ngModel)]="Order.reference" name="reference">
  </div>
  <div class="form-group">
    <label for="sender">Order Price</label>
    <input type="text" class="form-control" id="orderPrice" [(ngModel)]="Order.orderPrice" name="orderPrice">
  </div>

  <button type="submit" class="btn btn-primary">Submit</button>
</form>
