import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Produit } from 'src/app/models/produit';
import { ProduitService } from 'src/app/services/product.service';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.scss']
})
export class AddProductComponent  {
  product : Produit = new Produit() ; 

  uploadedImage: File;
  dbImage: any;
  postResponse: any;
  successResponse: string;
  image: any;

  constructor(private produitService: ProduitService) { }

  saveProduit(){  
    this.product;
   /*  const imageFormData = new FormData();
    imageFormData.append('image', this.uploadedImage, this.uploadedImage.name);
    this.product.image = imageFormData; */
    console.log(this.product)
    this.produitService.addProduct(this.product).subscribe(
      data => {
        console.log(data)    

      },error => console.log(error)
      );  


    }

    public onImageUpload(event) {
      this.uploadedImage = event.target.files[0];
    }

}
<div class="container">
  <h2>Add a New Product</h2>
  <form #f="ngForm" (submit)="saveProduit()">
    <div class="form-group">
      <label for="name">Name:</label>
      <input type="text" class="form-control" id="name" name="name" [(ngModel)]="product.name" required>
    </div>
    <div class="form-group">
      <label for="description">Description:</label>
      <textarea class="form-control" id="description" name="description" [(ngModel)]="product.description" required></textarea>
    </div>
   <div class="form-group">
      <label for="price">Price:</label>
      <input type="number" class="form-control" id="price" name="price" [(ngModel)]="product.price" required>
    </div>
    <div class="form-group">
      <label for="stock">Stock:</label>
      <input type="number" class="form-control" id="stock" name="stock" [(ngModel)]="product.stock" required>
    </div> 
    <div class="form-group">
      <label for="category">Category:</label>
      <input type="number" class="form-control" id="idCategorie" name="idCategorie" [(ngModel)]="product.idCategorie" required>  <!-- <select class="form-control" formControlName="category" id="category">
        <option value="">-- Select a category --</option>
        <option *ngFor="let category of categories" [value]="category.id">{{ category.name }}</option>
      </select> -->
    </div>
   <div class="form-group"> 
       <label for="image">Image:</label> 
       <input type="file" class="form-control-file" id="image" name="image" accept="image/*" (change)="onImageUpload($event)"> 
     </div> 
    <button type="submit" class="btn btn-primary">Add Product</button>
  </form>
</div>
