import { Component } from '@angular/core';
import { Category } from 'src/app/models/category';
import { CategoryService } from 'src/app/services/category.service';

@Component({
  selector: 'app-add-category',
  templateUrl: './add-category.component.html',
  styleUrls: ['./add-category.component.scss']
})
export class AddCategoryComponent {
  category :Category  = new Category();
  constructor (private categoryservice:CategoryService){}
  saveCategory(){  
    this.category;
    this.categoryservice.addCategory(this.category).subscribe(
      data => {
        console.log(data)    

      },error => console.log(error)
      );  


    }

}
<div class="container">
    <h2>Add a New Product</h2>
    <form #f="ngForm" (submit)="saveCategory()">
      <div class="form-group">
        <label for="name">Name:</label>
        <input type="text" class="form-control" id="name" name="name" [(ngModel)]="category.name" required>
      </div>
      <div class="form-group">
        <label for="description">Description:</label>
        <textarea class="form-control" id="description" name="description" [(ngModel)]="category.description" required></textarea>
      </div>
      <button type="submit" class="btn btn-primary">Add Category</button>
      </form>
  </div>
