import { Component, OnInit } from '@angular/core';
import { ProduitService } from 'src/app/services/product.service';
import { ActivatedRoute, Router  } from '@angular/router';
import { Produit } from 'src/app/models/produit';
@Component({
  selector: 'app-edit-product',
  templateUrl: './edit-product.component.html',
  styleUrls: ['./edit-product.component.scss']
})
export class EditProductComponent implements OnInit {

  produitId!: number;
  produit!: Produit;

  constructor(
    private route: ActivatedRoute,
    private produitService: ProduitService,
    private router: Router,

  ) { }
  ngOnInit(): void {
    this.produitId = parseInt(this.route.snapshot.paramMap.get('id') ?? '0');
    this.loadProduit();
  }

  loadProduit(): void {
    this.produitService.retrieveProduct(this.produitId).subscribe(
      produit => {
        this.produit = produit;
        console.log("produit loaded successfully.", produit);
      },
      error => console.log(error)
    );
  }

  updateProduit(): void {
    this.produitService.updateProduct(this.produit).subscribe(
      response => {
        console.log("produit updated successfully.", response);
        this.router.navigate(['/produit-list']);
      },

      error => console.log(error)
    );
  }
}
