import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Shop } from 'src/app/models/shop';
import { CategoryService } from 'src/app/services/category.service';
import { ShopService } from 'src/app/services/shop.service';
@Component({
  selector: 'app-edit-shop',
  templateUrl: './edit-shop.component.html',
  styleUrls: ['./edit-shop.component.scss']
})
export class EditShopComponent implements OnInit {
  idShop: number;
  shop: Shop;

  constructor(
    private route: ActivatedRoute,
    private shopservice: ShopService,
    private router: Router,

  ) { }

  ngOnInit(): void {
    this.idShop = parseInt(this.route.snapshot.paramMap.get('id') ?? '0');
    this.loadShop();
  }

  loadShop(): void {
    this.shopservice.retrieveShop(this.idShop).subscribe(
      shop => {
        this.shop = shop;
        console.log("shop loaded successfully.", shop);
      },
      error => console.log(error)
    );
  }
  updateShop(): void {
    console.log(this.shop)
    this.shopservice.updateShop(this.shop).subscribe(
      response => {
        console.log("shop updated successfully.", response);
        this.router.navigate(['/list-shops']);
      },

      error => console.log(error)
    );
  }

}

<div class="container">
  <h2>Edit Categories</h2>
  <form #f="ngForm"  (ngSubmit)="updateShop()">
      <div class="form-group">
        <label for="name">Name</label>
         <app-map></app-map>
        <input type="text" class="form-control" #name="ngModel" id="name" name='name' [(ngModel)]="shop.name">
      </div>

      <div class="form-group">
        <label for="address">address</label>
        <textarea class="form-control" id="address" rows="3" name='address' [(ngModel)]="shop.address"></textarea>
      </div>
      <button type="submit" class="btn btn-primary" [disabled]="f.invalid">Update</button>
    </form>
</div>
