import { Component, OnInit } from '@angular/core';
import { Produit } from 'src/app/models/produit';
import { ProduitService } from 'src/app/services/product.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-product',
  templateUrl: './productList.component.html',
  styleUrls: ['./productList.component.scss']
})
export class ProductComponent implements OnInit {

  produits: Produit[] = [];
  p: number = 1;
  itemsPerPage:number = 8;
  totalProducts: any;

  constructor(private produitService: ProduitService, private router : Router) { }

  ngOnInit(): void {
    this.loadProduits();
  }
  loadProduits(): void {
    this.produitService.getProducts().subscribe(data => {
      this.produits = data;
      this.totalProducts = data.length;
    });
  }
  changeCurrency(): void {

this.produitService.getProducts("TND").subscribe(
      response => {this.produits = response;
        this.totalProducts = response.length;
      },
      error => console.log(error)
    );}
    changeCurrencyToEuro(): void {

      this.produitService.getProducts("EUR").subscribe(
            response => {this.produits = response;
              this.totalProducts = response.length;
            },
            error => console.log(error)
          );}
  deleteProduit(id: number): void {
    if (confirm("Are you sure you want to delete this Product?")) {
      console.log(id)
      this.produitService.removeProduct(id).subscribe(
        response => {
          console.log("Product deleted successfully.");
          this.loadProduits();
          window.location.reload();
        },
        error => console.log(error)
      );
    }
  }
