import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Produit } from 'src/app/models/produit';
import { ProduitService } from 'src/app/services/product.service';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.scss']
})
export class AddProductComponent  {
  product : Produit = new Produit() ; 

  // image!: File ;

  constructor(private produitService: ProduitService) { }

  saveProduit(){  
    this.product;
    console.log(this.product)
    this.produitService.addProduct(this.product).subscribe(
      data => {
        console.log(data)    

      },error => console.log(error)
      );  



  // onFileSelected(event: Event) {
  //   const target = event.target as HTMLInputElement;

  //   // Do something with the selected files...
  // }


}
}
<div class="container">
  <h2>Add a New Product</h2>
  <form #f="ngForm" (submit)="saveProduit()">
    <div class="form-group">
      <label for="name">Name:</label>
      <input type="text" class="form-control" id="name" name="name" [(ngModel)]="product.name" required>
    </div>
    <div class="form-group">
      <label for="description">Description:</label>
      <textarea class="form-control" id="description" name="description" [(ngModel)]="product.description" required></textarea>
    </div>
    <!-- <div class="form-group">
